import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import Login from "./components/Login";
import CrearUnirseSala from "./components/CrearUnirseSala"; // Asumiendo que existe este componente
import Chat from "./components/Chat"; // Asumiendo que existe este componente

const App = () => {
    const [username, setUsername] = useState("");
    const navigate = useNavigate();

    const handleRegister = (username) => {
        setUsername(username);
        navigate("/crear-unirse-sala"); // Navegar a la siguiente pantalla
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login onRegister={handleRegister} />} />
                <Route path="/crear-unirse-sala" element={<CrearUnirseSala username={username} />} />
                <Route path="/chat" element={<Chat username={username} />} />
            </Routes>
        </Router>
    );
};

export default App;
