import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './Login';  // Ruta de login
import CrearUnirseSala from './CrearUnirseSala';  // Pantalla para elegir sala o crear una
import Chat from './Chat';  // Pantalla de chat

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login />} /> {/* Ruta de login */}
                <Route path="/seleccionar-opcion" element={<CrearUnirseSala />} /> {/* Ruta para crear o unirse a una sala */}
                <Route path="/chat" element={<Chat />} /> {/* Ruta de chat */}
            </Routes>
        </Router>
    );
}

export default App;
