import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom"; // Asegúrate de importar esto
import Home from "./components/Home";
import CrearUnirseSala from "./components/CrearUnirseSala"; // Tu componente para crear o unirse a salas
import Chat from "./components/Chat"; // Tu componente de chat

function App() {
    const [username, setUsername] = useState("");
    const navigate = useNavigate(); // Usamos el hook 'useNavigate' para redirigir

    const handleRegister = (username) => {
        if (username.trim() === "") {
            alert("Por favor ingresa un nombre de usuario");
            return;
        }
        setUsername(username);
        // Después de registrar, redirigimos a la página de "crear o unirse a sala"
        navigate("/crear-unirse-sala");
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home onRegister={handleRegister} />} />
                <Route path="/crear-unirse-sala" element={<CrearUnirseSala username={username} />} />
                <Route path="/chat" element={<Chat username={username} />} />
            </Routes>
        </Router>
    );
}

export default App;
