// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import CrearUnirseSala from './components/CrearUnirseSala';
import Chat from './components/Chat';

function App() {
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    const handleRegister = (username) => {
        setUsername(username);
        navigate('/crear-unirse-sala');
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login onRegister={handleRegister} />} />
                <Route path="/crear-unirse-sala" element={<CrearUnirseSala username={username} />} />
                <Route path="/chat" element={<Chat username={username} />} />
            </Routes>
        </Router>
    );
}

export default App;
