import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Login from './components/Login';
import CrearUnirseSala from './components/CrearUnirseSala';
import Chat from './components/Chat';
import Navbar from './components/Navbar';  // Asegúrate de tener este componente si lo usas
import ChatList from './components/ChatList';  // Asegúrate de tener este componente si lo usas
import Home from './components/Home';  // Asegúrate de tener este componente si lo usas
import RoomCreate from './components/RoomCreate';  // Asegúrate de tener este componente si lo usas

function App() {
    return (
        <div className="App">
            <Navbar /> {/* Si usas Navbar */}
            <Routes>
                <Route path="/" element={<Home />} /> {/* Redirigir a Home o Login dependiendo del flujo */}
                <Route path="/login" element={<Login />} />
                <Route path="/seleccionar-opcion" element={<CrearUnirseSala />} />
                <Route path="/chat" element={<Chat />} />
                <Route path="/room-create" element={<RoomCreate />} /> {/* Agregado para la creación de salas */}
            </Routes>
        </div>
    );
}

export default App;
