import React, { useState } from "react";

const CrearUnirseSala = ({ username }) => {
    const [roomName, setRoomName] = useState("");
    const [isCreating, setIsCreating] = useState(false);

    const handleCreateRoom = () => {
        if (roomName.trim() === "") {
            alert("Por favor ingresa el nombre de la sala");
            return;
        }
        // Aquí puedes agregar lógica para crear la sala
        console.log(`Sala ${roomName} creada`);
    };

    const handleJoinRoom = () => {
        if (roomName.trim() === "") {
            alert("Por favor ingresa el nombre de la sala");
            return;
        }
        // Aquí puedes agregar lógica para unirte a la sala
        console.log(`Unido a la sala ${roomName}`);
    };

    return (
        <div>
            <h1>Bienvenido, {username}</h1>
            <div>
                <button onClick={() => setIsCreating(true)}>Crear Sala</button>
                <button onClick={() => setIsCreating(false)}>Unirse a Sala</button>
            </div>
            {isCreating ? (
                <div>
                    <h2>Crea una nueva sala</h2>
                    <input
                        type="text"
                        placeholder="Ingresa el nombre de la sala"
                        value={roomName}
                        onChange={(e) => setRoomName(e.target.value)}
                    />
                    <button onClick={handleCreateRoom}>Crear Sala</button>
                </div>
            ) : (
                <div>
                    <h2>Unirse a una sala</h2>
                    <input
                        type="text"
                        placeholder="Ingresa el nombre de la sala"
                        value={roomName}
                        onChange={(e) => setRoomName(e.target.value)}
                    />
                    <button onClick={handleJoinRoom}>Unirse</button>
                </div>
            )}
        </div>
    );
};

export default CrearUnirseSala;
