import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


const Home = ({ onRegister }) => {
    const [username, setUsername] = useState("");
    const navigate = useNavigate();

    const handleRegister = () => {
        if (username.trim() === "") {
            alert("Por favor ingresa un nombre de usuario");
            return;
        }
        onRegister(username); // Pasar el nombre de usuario a la pantalla de chat
        navigate("/seleccionar-opcion"); // Redirigir a la pantalla de selección de sala
    };

    return (
        <div className="home-container">
            <div className="welcome-text">
                <h1>Bienvenido al Chat</h1>
                <p>¡Conéctate con tus amigos y comparte tus ideas en tiempo real!</p>
            </div>
            <div className="login-container">
                <input
                    type="text"
                    placeholder="Ingresa tu nombre de usuario"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="input"
                />
                <button onClick={handleRegister} className="login-btn">
                    Unirse
                </button>
            </div>
        </div>
    );
};

export default Home;
