import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Importamos el hook para redirigir

const Login = () => {
    const [name, setName] = useState('');
    const navigate = useNavigate(); // Inicializamos el hook useNavigate

    const handleLogin = () => {
        if (name) {
            // Redirige a la siguiente pantalla (selección de sala)
            navigate('/seleccionar-opcion');
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}  // Actualiza el nombre del usuario
                placeholder="Ingresa tu nombre"
            />
            <button onClick={handleLogin}>Entrar</button> {/* Al hacer clic, redirige */}
        </div>
    );
};

export default Login;
